mapper.insert:


    SELECT TB_LOG_SEQ.NEXTVAL FROM DUAL


INSERT INTO TB_LOG (
    LOG_ID, IMAGE_PATH, LOG_TITLE, LOG_START_DATE, LOG_END_DATE, PLAN_DAY, USER_ID, CREATED_AT, UPDATED_AT
) VALUES (
    #{logId}, #{imagePath}, #{logTitle}, #{logStartDate}, #{logEndDate}, #{planDay}, #{userId}, SYSDATE, SYSDATE
)

mapper.select:
	SELECT COUNT(*) 
   FROM TB_LOG l


           WHERE l.ACCO_ID = #{locId}


           WHERE r.REST_ID = #{locId}


           WHERE r.ATTR_ID = #{locId}


           WHERE 1 = 0

SELECT r.REVIEW_ID, r.USER_REV, r.ACCO_ID, r.REST_ID, r.ATTR_ID, r.USER_REVIEW_TEXT, 
       r.CREATED_AT, r.USER_ID, 
       u.USER_NICK  
FROM TB_REVIEW r
JOIN TB_USER u ON r.USER_ID = u.USER_ID 


        WHERE r.ACCO_ID = #{locId}


        WHERE r.REST_ID = #{locId}


        WHERE r.ATTR_ID = #{locId}




        ORDER BY r.CREATED_AT DESC
